const pc_t L5 = 33;
const pc_t L4 = 44;
const pc_t L3 = 47;
const pc_t L6 = 57;
const pc_t L7 = 63;
const pc_t L8 = 65;
const pc_t L12 = 84;
const pc_t L11 = 95;
const pc_t L10 = 98;
const pc_t L13 = 108;
const pc_t L14 = 114;
